import React, { useState } from 'react';
import Row from "react-bootstrap/Row"
import Col from "react-bootstrap/Col"

function Search(){
    const [search, setSearch] = useState('');

    const clearSearch = () => {
        setSearch('');
    };

    return (
        <div>
            <h1>Just imagine the search box works</h1>
            <div className="input-group rounded">
                <input type="search" className="form-control rounded" placeholder="Search for a Company here!"
                    value={search} onChange={e => setSearch(e.target.value)}/>
                <span className="input-group-text border-0" id="search-addon" onClick={clearSearch}>
                    <i className="fas fa-search"></i>
                </span>
            </div>
        </div>
    )
}

export default Search;
